css:
/*pc*/


@media (min-width: 1280px){.topo{ display: block!important;background:#f4e5f1;
        background-size: auto auto;
    color: #6f6057;
  background-size: 100%;}}


/*mobile*/
@media (max-width: 1009px) {.topo{ display:none!important;}
  }

/*responsivo*/

body{font-family:'Quicksand',sans-serif; font-size:16px; color:#352727;}
.top-bar a.top-bar__countdown {
    background:#dc83ec;
    color: var(--primary-yiq);font-family:'Quicksand',sans-serif;font-size:20px;font-weight: 700;
}
.lead {
  font-size: 0.75rem;}
h1 {
    font-size: 1.2rem;
}

.shipping-line font-size: 0.7rem;
}
.product-card__name{text-align: center!important;font-size: 1.2rem!important;}
.header {
    border-width: 2px;
    padding: var(--spacer-2) var(--spacer-3);
}
.product__sku{font-size: 0.5rem;}
.header__buttons .btn {
    font-size: 1.3rem;color:#333333;
}
.header__toggler-icon {
  color: #2f2f2f;}
.header__toggler {
  color: #632e57;}
.d-lg-inline{ font-weight: 700;}
.header__buttons .btn{color:#000;}
.header__search-btn{color:#ebc32f;}
.header__search-btn hover{color:#5e1963;}
.badge-primary {
    color: #fff;
    background-color: #2f94d6;
}
.fa-facebook{ font-size:1.5rem;}
.fa-youtube{ font-size:1.5rem;}
.fa-instagram{ font-size:1.5rem;}
.form-control-lg {
    height: calc(1.5em + 1rem + 2px);
    padding: .5rem 1rem;
    font-size: 0.75rem;
    line-height: 1.5;
    border-radius: .3rem;
}
.search__submit{color: #632f69;}
.info-bar__item {
    text-transform: none;
  font-weight: 700;}
.footer {
     background: #fff url(https://cdn.ecvol.com/s/www.coisasdatecaloja.com.br/uploads/ff4.png);
        background-size: auto auto;
    color: #6f6057;
    background-size: 100%;
    font-weight: 500;}
.footer__links {
    font-size: 84%;
}
.footer__store {
    color: #632e57;
    font-weight: 700;
}
.footer__title {
    font-size: 1.5rem;
    font-weight: 500;
    margin-bottom: var(--spacer-2);
    display: block;
    color: #632e57;
    outline: none;
}
.footer__credits{font-size: 1.2rem;border-top: var(--border-width) solid #dee2e600;}
.ecom-credits {
    padding: 1.5rem;
    background: #ececec;
    text-align: center;
    font-size: .85rem;
    color: #37003c;
}
.page-title__head {
     font-weight: 700;font-size:1.5rem;
  color: #632e57;}
.header__nav a { text-transform:none;  font-weight: 700;padding: 13px 32px 0px;}
.header__nav a:hover {
    font-weight: 700;
    color: #fff;background:#42d2e8;
    padding: 13px 32px 0px;
    border-radius: 0px !important;
}
.header__nav {
    text-align: center;
    font-size: 1.8em;
    margin-top: 15px;padding: 10px 10px 10px;
}
.menu__item:hover { background:#60cad6;}
.btn-primary {color: #fff;
    background-color:  #2abfcf;
    border-color:  #2abfcf;}
.btn-primary:hover {color: #fff;
    background-color:  #b76eca;
    border-color: #b76eca;}
.btn-lg {
   color: #000;
    background-color: #ffe64c;
    border-color: transparent;
}
.btn-lg:hover{
  color:#fff;
     background-color:#c2a2ca;
    border-color: #c2a2ca;
}

.payment-option__label {
  display: none;}
h2 {
    font-size: 1.3rem;
}
h4 {
    font-size: 1rem;
}
.search-engine__toggles {color: #8c8c8c;
}
.btn-secondary {
    color: #464646;
    background-color: #e0c6e4;
    border-color: #e0c6e4;
}
#collapse-tamanho_tecido{display:none;}

.card-footer {
    padding: .75rem 1.25rem;
    background-color: rgb(238 227 239 / 70%);
    border-top: 1px solid rgb(247 246 246);
}

.cart__total .prices__value {
    font-size: 1rem;
}
.app__header{background: #f4eaf5;}
.minicart__subtotal {
    text-align: right;
    color: #4a4a4a;
}
.products-carousel__title {
  font-weight: 700; font-size:1.4rem;}

#header{ display: block!important;background: #fff url(https://cdn.ecvol.com/s/www.coisasdatecaloja.com.br/uploads/fundofinal1.png);
        background-size: auto auto;
    color: #6f6057;
    background-size: 100%;}

.form-control {
    display: block;width:90%;
  font-size: 0.6rem;}
.btn-outline-secondary {
    color: #000;
    border-color: #ffbe00;background:#ffbe00;
}
.prices__value {
    display: block;
    color: #5f0f4d;font-size: 1.6rem;
}
.product-item{color:#000;}
.fa-search{font-size:1.3rem;}
.mt-lg-0{margin-left: 190px;
}
.top-bar{display:none;}
.top-bar__countdown{display:none;}
.header__search-btn{right: 80px;}
.pb-lg-4{padding-bottom: 0rem;}
.footer__title[aria-expanded] {
    border-bottom: var(--border-width) solid #dee2e600;
  border-top: var(--border-width) solid #dee2e600;}
.product__payment card{display:none;}
}
