css:
body{font-family:'Quicksand',sans-serif; font-size:16px; color:#352727;}
.top-bar a.top-bar__countdown {
    background:#dc83ec;
    color: var(--primary-yiq);font-family:'Quicksand',sans-serif;font-size:20px;font-weight: 700;
}
.header {
    background: #ead0e5;
}
h1 {
    font-size: 1.5rem;
}
.header {
    border-width: 2px;
    padding: var(--spacer-2) var(--spacer-3);
}
.header__toggler-icon {
  color: #2f2f2f;}
.header__toggler {
  color: #632e57;}
.d-lg-inline{ font-weight: 700;}
.header__buttons .btn{color:#632e57;}
.header__search-btn{color:#3c3c3c;}
.header__search-btn hover{color:#5e1963;}
.badge-primary {
    color: #fff;
    background-color: #2f94d6;
}
.fa-facebook{ font-size:20px;}
.fa-youtube{ font-size:20px;}
.fa-instagram{ font-size:20px;}
.form-control-lg {
    height: calc(1.5em + 1rem + 2px);
    padding: .5rem 1rem;
    font-size: 0.75rem;
    line-height: 1.5;
    border-radius: .3rem;
}
.search__submit{color: #632f69;}
.info-bar__item {
    text-transform: none;
  font-weight: 700;}
.footer {
    background: #e2e2e2;
    border-top: var(--spacer-1) solid #632e57;
    color: #323538; font-weight: 500;}
.footer__links {
    font-size: 84%;
}
.footer__store {
    color: #632e57;
    font-weight: 700;
}
.footer__title {
    font-size: 20px;
    font-weight: 500;
    margin-bottom: var(--spacer-2);
    display: block;
    color: #632e57;
    outline: none;
}
.footer__credits{font-size: 15px;}
.ecom-credits {
    padding: 1.5rem;
    background: #d0d0d0;
    text-align: center;
    font-size: .85rem;
    color: #37003c;
}
.page-title__head {
     font-weight: 700;font-size:22px;
  color: #632e57;}
.header__nav a { text-transform:none;  font-weight: 700;}
.menu__item:hover { background:#60cad6;}
.btn-primary {color: #fff;
    background-color:  #2abfcf;
    border-color:  #2abfcf;}
.btn-lg {
    color: #fff;
    background-color: #60cad6;
    border-color: #60cad6;
}
.btn-lg:hover{
    color: #fff;
    background-color: #a467b1;
    border-color: #a467b1;
}
.header__nav {
    text-align: center;
    font-size: 1.8em;
    line-height: var(--line-height-lg);
}

.payment-option__label {
  display: none;}
h2 {
    font-size: 1.3rem;
}
h4 {
    font-size: 1rem;
}
.search-engine__toggles {color: #8c8c8c;
}
.btn-secondary {
    color: #464646;
    background-color: #e0c6e4;
    border-color: #e0c6e4;
}
#collapse-tamanho_tecido{display:none;}

.btn-outline-secondary {
    color: #e0c6e4;
    border-color: #e0c6e4;
}
.card-footer {
    padding: .75rem 1.25rem;
    background-color: rgb(238 227 239 / 70%);
    border-top: 1px solid rgb(247 246 246);
}

.cart__total .prices__value {
    font-size: 1rem;
}
.app__header{background: #f4eaf5;}
.minicart__subtotal {
    text-align: right;
    color: #4a4a4a;
}
.btn-outline-secondary {
    color: #5c315d;
    border-color: #753673;
}
.products-carousel__title {
  font-weight: 700; font-size:1.4rem;}

.d-lg-block{ display: block!important;background: #fff url(https://cdn.ecvol.com/s/www.coisasdatecaloja.com.br/uploads/fundofinal1.png);
        background-size: auto auto;
    color: #6f6057;
    background-size: 100%;}
