css:
body{font-family:'Quicksand',sans-serif; font-size:16px; color:#352727;}
.top-bar a.top-bar__countdown {
    background:#a78ea4;
    color: var(--primary-yiq);font-family:'Quicksand',sans-serif;font-size:20px;font-weight: 700;
}
.header{background: #e4c9de;}
.header__toggler-icon {
  color: #2f2f2f;}
.header__toggler {
  color: #632e57;}
.d-lg-inline{ font-weight: 700;}
.header__buttons .btn{color:#632e57;}
.header__search-btn{color:#3c3c3c;}
.header__search-btn hover{color:#5e1963;}
.badge-primary {
    color: #fff;
    background-color: #2f94d6;
}
.fa-facebook{ font-size:20px;}
.fa-youtube{ font-size:20px;}
.fa-instagram{ font-size:20px;}
.form-control-lg {
    height: calc(1.5em + 1rem + 2px);
    padding: .5rem 1rem;
    font-size: 0.75rem;
    line-height: 1.5;
    border-radius: .3rem;
}
.search__submit{color: #632f69;}
.info-bar__item {
    text-transform: none;
  font-weight: 700;}
.footer {
    background: #e2e2e2;
    border-top: var(--spacer-1) solid #632e57;
    color: #323538; font-weight: 500;}

.footer__store {
    color: #632e57;
    font-weight: 700;
}
.footer__title {
    font-size: var(--font-size-lg);
    font-weight: 500;
    margin-bottom: var(--spacer-2);
    display: block;
    color: #632e57;
    outline: none;
}
